mapper.select:
SELECT p.id, q.HOST host, q.APPLICATION_SERVER applicationServer, q.USER_NAME userName, q.DB_USERNAME dbUsername, p.SYS_VERSION sysVersion, q.SUBJECT subject, q.SYSTEM_TYPE systemType, q.CRONTAB crontab, q.CREATION_DATE createTime, q.EXEC_TIME execTime FROM dc_server_setting q LEFT JOIN dc_distribute_setting p ON q. HOST = p.IP WHERE q.IS_DELETE = 0
AND host LIKE "%"#{host}"%"
LIMIT #{offSet}, #{pageRow}

mapper.update:
UPDATE DC_SERVER_SETTING SSH
SET    SSH.IS_DELETE = '1'
WHERE  SSH.ID = #{id}
