mapper.select:
SELECT ssh.id id, ssh.code code, (SELECT MAX(code) FROM dc_shell_setting) maxCode, ssh.shell_name shellName, ssh.shell_desc shellDesc, ssh.system_type systemType, ssh.system_version systemVersion, date_format(ssh.create_time, '%Y-%m-%d %T') createTime FROM dc_shell_setting ssh WHERE ssh.is_delete = '0'
AND ssh.shell_desc LIKE "%"#{shellDesc}"%"
LIMIT #{offSet}, #{pageRow}

mapper.update:
UPDATE dc_shell_setting ssh
SET ssh.is_delete = '1'
WHERE ssh.id = #{id}
