mapper.select:
SELECT p.id, q.host host, q.service_name serviceName, q.post post, q.application_server applicationServer, q.user_name userName, q.password password, q.db_username dbUsername, q.db_password dbPassword, p.sys_version sysVersion, q.subject subject, q.system_type systemType, q.crontab crontab, q.creation_date createTime, q.exec_time execTime, q.time_out timeOut, q.crontab cron, 'group', q.class_path classPath FROM dc_server_setting q LEFT JOIN dc_distribute_setting p ON q. HOST = p.IP AND q.system_type = p.system_type WHERE q.is_delete = 0 AND p.is_delete = 0
AND host LIKE "%"#{host}"%"
AND p.id = #{id}
LIMIT #{offSet}, #{pageRow}
