mapper.select:
SELECT ssh.id id, ssh.IP IP, ssh.host_name hostName, ssh.sys_version sysVersion, ssh.system_type systemType, ssh.exec_time execTime, date_format(ssh.create_time, '%Y-%m-%d %T') createTime FROM dc_distribute_setting ssh WHERE ssh.IS_DELETE = '0'
AND ssh.IP LIKE "%"#{IP}"%"
LIMIT #{offSet}, #{pageRow}

mapper.update:
UPDATE DC_DISTRIBUTE_SETTING SSH
SET SSH.IS_DELETE = '1'
WHERE SSH.ID = #{id};

UPDATE DC_SERVER_SETTING DSS
SET    DSS.IS_DELETE = '1'
WHERE  DSS.HOST = (SELECT  SSH.IP
                    FROM   DC_DISTRIBUTE_SETTING SSH
                    WHERE  SSH.ID = #{id}
                    AND    DSS.SYSTEM_TYPE = SSH.SYSTEM_TYPE);
